mapper.select:
select ta.business_id,ta.project_name from (select * from view_project_review t where t.report_create_date is null and t.TYPE=#{type} and t.WF_STATE!='0'
and exists (select * from rcm_project_relation r where t.business_id = r.business_id and r.user_id = '${user_id}')
union select * from view_project_review v where v.report_create_date is null and v.WF_STATE!='0' and v.TYPE=#{type}
and exists (select * from rcm_project_relation r where r.business_id = v.BUSINESS_ID and r.relation_type = '0706' and r.user_id = '${user_id}')
) ta, (select * from rcm_project_relation where relation_type = '0706') r where ta.business_id = r.business_id(+) and r.user_name is not null

select tt.*, to_char(complete_date, 'yyyy-mm-dd hh24:mi:ss') as report_commit_date, (select o.name from sys_org o where o.orgpkvalue = pertainareaId ) as pertainareaName from (select ta.*, r.user_id as review_leader_id, r.user_name as review_leader_name from (select * from view_project_review t where t.report_create_date is not null and t.TYPE='${type}' and t.WF_STATE in ('2','3')
and exists (select * from rcm_project_relation r where t.business_id = r.business_id and r.user_id = '${user_id}')
union select * from view_project_review v where v.report_create_date is not null and v.WF_STATE in ('0','1') and v.TYPE='${type}'
and exists (select * from rcm_project_relation r where r.business_id = v.BUSINESS_ID and r.relation_type = '0706' and r.user_id = '${user_id}')
) ta, (select * from rcm_project_relation where relation_type = '0706') r where ta.business_id = r.business_id(+)) tt where 1 = 1 and tt.project_name like '%${PROJECT_NAME}%' and tt.project_model_names like '%${PROJECT_MODEL_NAMES}%' and to_char(tt.apply_date,'yyyy-mm-dd') = '${APPLY_TIME}'
and tt.wf_state in (${WF_STATE}) order by '1' ,tt.CREATE_DATE ${ASCDESC} ,tt.CREATE_DATE desc ,tt.wf_state ${WFSTATEASCDESC}

mapper.update:
update rcm_project_info set last_update_date=sysdate
 , wf_state=#{wfState}
 , apply_date=#{applyDate}
 , complete_date=#{completeDate}
, report_create_date=#{reportCreateDate}
, controller_val=#{controllerVal}
, report_id=#{reportId}
, need_meeting=#{needMeeting}
, meeting_date=#{meetingDate}
, emergencyLevel=#{emergencyLevel}
, isUrgent=#{isUrgent}
 where business_id=#{businessId}

mapper.select:
select * from view_project_review v where 1=1
 and v.report_id=#{reportId}
 and v.business_id=#{businessId}
