mapper.select:
/*正式评审审批*/
select lg.*, lg.projectname as "projectName" from rcm_formalassessment_info lg where lg.stage = '1' and lg.businessid = #{id}
/*投标评审审批*/
select lg.*, lg.projectname as "projectName" from rcm_pre_info lg where lg.stage = '1' and lg.businessid = #{id}
/*其它评审审批*/
select lg.*, lg.bulletinname as "projectName" from rcm_bulletin_info lg where lg.stage = '1' and lg.businessid = #{id}

/*正式评审审批*/
