mapper.select:
select

from sys_message
where 1 = 1

    and proc_inst_id = #{procInstId}


    and parent_id = #{parentId}

order by message_priority desc,message_date asc

select

from sys_message
where 1 = 1
and parent_id = #{parentId}
or original_id = #{parentId}
order by message_date asc

mapper.delete:
delete from sys_message m
where m.message_id = #{messageId}
or m.original_id = #{messageId}

mapper.select:
select

from sys_message
where 1 = 1

and proc_inst_id = #{procInstId}


and created_by = #{createdBy}


    and created_by  <>  #{createdBy}

order by message_priority desc, message_id, message_date asc

select

from sys_message
where 1 = 1

and proc_inst_id = #{procInstId}


and original_id = #{messageId}

order by message_id, message_date asc

select

from sys_message
where 1 = 1

    and proc_inst_id = #{procInstId}


    and parent_id = #{parentId}

order by message_priority desc,message_id, message_date asc

select
from sys_message m where m.parent_id = 0 and m.via_users is not null and instr(m.via_users, #{curUuid}) > 0 union select
from sys_message m where m.parent_id <> 0 and m.created_by = #{curUuid}

select tmp.* from ( select decode(u.orgcode, '000001', o2.name, o1.name) as orgname, u.* from sys_user u, sys_org o1, sys_org o2 where 1 = 1 and u.state = '2' and u.orgpkvalue = o1.orgpkvalue(+) and u.deptpkvalue = o2.orgpkvalue(+) and exists ( select 1 from sys_dictionary sd, sys_dictionary_item st where sd.dictionary_code in('REGIONAL_INFORMER', 'INSPECTION_TEAM') and sd.uuid = st.fk_dictionary_uuid and st.item_code = u.uuid
/*正式评审*/
union select 1 from rcm_formalassessment_info f where f.businessid = '${message_business_id}' and f.largeareapersonid = u.uuid union select 1 from rcm_formalassessment_info f where f.businessid = '${message_business_id}' and f.taskpersonid = u.uuid union select 1 from rcm_formalassessment_info f where f.businessid = '${message_business_id}' and f.legalreviewpersonid = u.uuid union select 1 from rcm_formalassessment_info f where f.businessid = '${message_business_id}' and f.reviewpersonid = u.uuid union select 1 from rcm_formalassessment_info f where f.businessid = '${message_business_id}' and f.grassrootslegalpersonid = u.uuid union select 1 from rcm_formalassessment_info f where f.businessid = '${message_business_id}' and f.firstlevellawyerpersonid = u.uuid union select 1 from rcm_formalassessment_log lg where lg.businessid = '${message_business_id}' and lg.audituserid = u.uuid
/*投标评审*/
union select 1 from rcm_pre_info p where p.businessid = '${message_business_id}' and p.largeareapersonid = u.uuid union select 1 from rcm_pre_info p where p.businessid = '${message_business_id}' and p.servicetypepersonid = u.uuid union select 1 from rcm_pre_info p where p.businessid = '${message_business_id}' and p.taskpersonid = u.uuid union select 1 from rcm_pre_info p where p.businessid = '${message_business_id}' and p.reviewpersonid = u.uuid union select 1 from rcm_pre_log lg where lg.businessid = '${message_business_id}' and lg.audituserid = u.uuid
/*其它评审*/
union select 1 from rcm_bulletin_info b where b.businessid = '${message_business_id}' and b.legalleaderid = u.uuid union select 1 from rcm_bulletin_info b where b.businessid = '${message_business_id}' and b.reviewleaderid = u.uuid union select 1 from rcm_bulletin_log lg where lg.businessid = '${message_business_id}' and lg.audituserid = u.uuid
) order by ${orderBy} ) tmp where 1 = 1 and tmp.name like '%${username}%' or tmp.account like '%${username}%' or tmp.orgname like '%${username}%'
