mapper.select:
select value , (select name from sys_org o where o.ORGPKVALUE = t.name ) name,t.name areaid  from (
	select count(*) value , pertainareaid name 
	from rcm_formalassessment_info where 1 = 1 and stage is not null
	 and wf_state in 
		
			#{s_x}
		
	
	 and stage in 
		
			#{s_x}
		
	
	 and stage in ('1','2','3','3.5','3.7','3.9','4','5','6','7','9')
	 and pertainareaid in 
		
			#{s_x}
		
	
	 and servicetype_id in 
		
			#{s_x}
		
	
	 
		and apply_date between to_date('${year}/01/01 00:00:00','YYYY/MM/dd hh24:mi:ss') 
		and to_date('${year}/12/31 23:59:59','YYYY/MM/dd hh24:mi:ss')
	
	
		and businessid in (
			select d.formal_id from rcm_decision_resolution d where d.formal_type = 0 and d.decision_result in 
			
			
			#{s_x}
			
		)
	
	 group by pertainareaid
) t

mapper.update:
update rcm_formalassessment_info
reviewpersonid = #{reviewLeader}, legalreviewpersonid = #{legalReviewLeader}, fixedgrouppersonids = #{fixedGroupIds} , professional_reviewers_ids = #{professionalReviewersIds} , largeareapersonid = #{largeAreaPersonId} , serviceTypePersonId = #{serviceTypePersonId} , firstLevelLawyerPersonId = #{firstLevelLawyerPersonId} , grassRootsLegalPersonId = #{grassRootsLegalPersonId} , taskPersonId = #{taskPersonId} , createBy = #{createBy} ,
where businessid=#{businessId}

mapper.select:
select * from ( select t.*, (select o.name from sys_org o where o.ORGPKVALUE=t.pertainareaid) pertainareaName , (select u.NAME from sys_user u where u.uuid=t.createby) investmentName, (select u.NAME from sys_user u where u.uuid=t.reviewpersonid) reviewpersonName , (select u.NAME from sys_user u where u.uuid=t.legalreviewpersonid) legalreviewpersonName, ( 'ProjectFormalReviewDetailView/view/'||t.businessid||'@'|| (select a.proc_def_id_ from act_hi_procinst a where a.proc_inst_id_ = hp.procinstid)||'@'||hp.procinstid||'@@' ) oldUrl from (select f.* from rcm_formalassessment_info f
where f.wf_state !='0' and f.createby = #{createBy} ) t, (select p.business_key_,MAX(p.proc_inst_id_) procinstid from act_hi_procinst p where p.proc_def_id_ in ( select id_ from act_re_procdef pf where pf.key_ in ('formalAssessment','formalReview') ) group by p.business_key_ ) hp where t.businessid = hp.business_key_ ) ta where 1 = 1 and ta.servicetype_id in (${serviceTypeId}) and ta.pertainareaName like '%${projectUnitName}%' and ta.projectName like '%${projectName}%' and ta.wf_state = #{wf_state}
order by ta.apply_date desc

select * from rcm_project_relation p 
where p.business_id = #{businessId} 
and p.relation_type = #{relationTypeId}

select * from rcm_project_info i  
where i.type = 'formalAssessment' and i.business_id in (${waitSql}) 
and i.business_id = #{businessId}

select * from ( select t.*, (select o.name from sys_org o where o.ORGPKVALUE = t.pertainareaid) pertainareaName, (select u.NAME from sys_user u where u.uuid = t.createby) investmentName, (select u.NAME from sys_user u where u.uuid = t.reviewpersonid) reviewpersonName, (select u.NAME from sys_user u where u.uuid = t.legalreviewpersonid) legalreviewpersonName, ('ProjectFormalReviewDetailView/view/' || t.businessid || '@' || (select a.proc_def_id_ from act_hi_procinst a where a.proc_inst_id_ = hp.procinstid) || '@' || hp.procinstid || '@@') oldUrl from ( select * from ( select f.* from rcm_formalassessment_info f ) tt ) t, ( select p.business_key_, MAX(p.proc_inst_id_) procinstid from act_hi_procinst p where p.proc_def_id_ in (select id_ from act_re_procdef pf where pf.key_ in ('formalAssessment', 'formalReview')) group by p.business_key_) hp where t.businessid = hp.business_key_(+)) ta where 1 = 1
and ta.PROJECTNAME like '%${project_name}%'
and ta.pertainareaName like '%${reporting_unit_name}%'
and ta.IS_SUPPLEMENT_REVIEW = ${is_supplement_review}
and ta.WF_STATE = ${wf_state}
order by
ta.APPLY_DATE desc,ta.WF_STATE desc
ta.APPLY_DATE ${orderby_apptime}
ta.WF_STATE ${orderby_state}
