mapper.select:
select * from (select org.name as areaname, p.pertainareaid, p.businessid, p.projectname project_name,p.servicetype_id service_type_id, to_char(p.create_date,'yyyy-mm-dd hh24:mi:ss') create_datea, decode(d.DECISION_RESULT,1,1,2,2,3,3,4,4, null,decode(n.CONSENTTOINVESTMENT, null, 0, 1,1,2,2,3,3,4,4)) being /* sum(decode(n.CONSENTTOINVESTMENT, null, 1, 0)) as being, sum(decode(d.DECISION_RESULT,1,1,decode(n.CONSENTTOINVESTMENT, 1, 1, 0))) as done1, sum(decode(d.DECISION_RESULT,2,1,decode(n.CONSENTTOINVESTMENT, 2, 1, 0))) as done2, sum(decode(d.DECISION_RESULT,3,1,decode(n.CONSENTTOINVESTMENT, 3, 1, 0))) as done3, sum(decode(d.DECISION_RESULT,4,1,decode(n.CONSENTTOINVESTMENT, 4, 1, 0))) as done4 */ from rcm_formalassessment_info p, (select * from RCM_DECISION_RESOLUTION where VOTE_STATUS = 2) d, rcm_noticedecision_info n, sys_org org where p.wf_state in ('1', '2') and p.pertainareaid is not null and p.businessid = d.formal_id(+) and p.businessid = n.projectformalid(+) and p.pertainareaid = org.orgpkvalue(+) ) where 1=1
and pertainareaid = #{pertainareaId}
and being = #{state}
order by create_datea desc

mapper.update:
update rcm_formalAssessment_report set 
projectSize = #{projectSize,jdbcType=DECIMAL},
investmentAmount = #{investmentAmount,jdbcType=DECIMAL},
rateOfReturn = #{rateOfReturn,jdbcType=DECIMAL},
serviceType = #{serviceType,jdbcType=VARCHAR}
where  businessId = #{businessId}
