mapper.select:
/*正式评审审批*/



/*投标评审审批*/



/*其它评审审批*/

/*正式评审审批*/
select lg.*, ac.* from rcm_formalassessment_log lg left join act_ru_task ac on ac.id_ = lg.taskid and ac.execution_id_ = lg.executionid where lg.iswaiting = '1' and lg.businessid = #{business_id}
/*投标评审审批*/
select lg.*, ac.* from rcm_pre_log lg left join act_ru_task ac on ac.id_ = lg.taskid and ac.execution_id_ = lg.executionid where lg.iswaiting = '1' and lg.businessid = #{business_id}
/*其它评审审批*/
select lg.*, ac.* from rcm_bulletin_log lg left join act_ru_task ac on ac.id_ = lg.taskid and ac.execution_id_ = lg.executionid where lg.iswaiting = '1' and lg.businessid = #{business_id}
