mapper.select:
SELECT RPI.BUSINESSID, RPI.PROJECTNAME FROM RCM_PRE_INFO RPI WHERE 1 = 1 AND RPI.STAGE = '3' AND RPI.REVIEWPERSONID = #{USERID,jdbcType=VARCHAR}
AND RPI.BUSINESSID IN
#{businessid,jdbcType=VARCHAR}
ORDER BY RPI.CREATE_DATE DESC

mapper.update:
UPDATE rcm_pre_report s
projectSize = #{projectSize,jdbcType=DECIMAL}, investmentAmount = #{investmentAmount,jdbcType=DECIMAL}, rateOfReturn = #{rateOfReturn,jdbcType=DECIMAL}, serviceType = #{serviceType,jdbcType=VARCHAR}, create_date = #{create_date,jdbcType=TIMESTAMP}, s.submit_date = #{submit_date,jdbcType=TIMESTAMP},
WHERE s.businessId = #{businessId,jdbcType=VARCHAR}

update rcm_pre_report set 
projectSize = #{projectSize,jdbcType=DECIMAL},
investmentAmount = #{investmentAmount,jdbcType=DECIMAL},
rateOfReturn = #{rateOfReturn,jdbcType=DECIMAL},
serviceType = #{serviceType,jdbcType=VARCHAR}
where  businessId = #{businessId}
